import React, { useState } from 'react'

import Fade from "react-reveal/Fade";

import "../css/Contact.css";
import Footer from "../Footer";


const Contact = () => {
  return(
    <>
      <ContactChoice />
      <Footer />
    </>
  )
}

const ContactChoice = () => {
	const [textA, setTextA] = useState('BUSINESS');
	const [textB, setTextB] = useState('FEEDBACK');
	const [enqAWidth, setEnqAWidth] = useState("50%");
	const [enqBWidth, setEnqBWidth] = useState("50%");

	const [showBus, setShowBus] = useState(false);
	const [showFeed, setShowFeed] = useState(false);

	return (
		<>
			<Fade cascade>
				<div className="choiceContainer">
					<div className="enqA" style={{ width: enqAWidth }} onClick={() => {setEnqAWidth("100%"); setEnqBWidth("0"); setTextB(""); setShowBus(!showBus)}}>
						<h1>{textA}</h1>
					</div>
					{showBus && <ContactFormBusiness />}

					<div className="enqB" style={{width: enqBWidth}} onClick={() => {setEnqBWidth("100%"); setEnqAWidth("0"); setTextA(""); setShowFeed(!showFeed)}}>
						<h1>{textB}</h1>
					</div>
					{showFeed && <ContactFormFeedback />}
				</div>
			</Fade>
		</>
	);
}

const ContactFormBusiness = () => {

	const [name, setName] = useState('');
	const [email, setEmail] = useState('');
	const [phone, setPhone] = useState('');
	const [prefContactMethod, setPrefContactMethod] = useState('');
	const [message, setMessage] = useState('');

	const handleSubmit = (e) => {
		e.preventDefault();
	}

	return (
		<>
			<article>
				<form className="form" onSubmit={handleSubmit}>
					<div className="form-control">
						<label>Name: </label>
						<input type="text" id="name" name="name" value={ name } onChange={(e) => setName(e.target.value)} />
					</div>

					<div className="form-control">
						<label>Email: </label>
						<input type="email" id="email" name="email" value={ email } onChange={(e) => setEmail(e.target.value)}/>
					</div>

					<div className="form-control">
						<label>Phone: </label>
						<input type="phone" id="phone" name="email" value={ phone } onChange={(e) => setPhone(e.target.value)}/>
					</div>

					<div className="form-control">
						<textarea id="message" name="message" value={ message } onChange={(e) => setMessage(e.target.value)}>Message...</textarea>
					</div>
					<button type="submit">Contact</button>
				</form>
			</article>
		</>
	)
}

const ContactFormFeedback = () => {

	const [name, setName] = useState('');
	const [email, setEmail] = useState('');
	const [message, setMessage] = useState('');

	const handleSubmit = (e) => {
		e.preventDefault();
	}

	return (
		<>
			<article>
				<form className="form" onSubmit={handleSubmit}>
					<div className="form-control">
						<label>Name: </label>
						<input type="text" id="name" name="name" value={ name } onChange={(e) => setName(e.target.value)} />
					</div>

					<div className="form-control">
						<label>Email: </label>
						<input type="email" id="email" name="email" value={ email } onChange={(e) => setEmail(e.target.value)}/>
					</div>

					<div className="form-control">
						<textarea id="message" name="message" value={ message } onChange={(e) => setMessage(e.target.value)}>Message...</textarea>
					</div>
					<button type="submit">Contact</button>
				</form>
			</article>
		</>
	)
}

export default Contact;
