import React, { useState } from 'react'
import { Message, Divider, Card, Button, Checkbox, Form, TextArea, Select, Confirm, Modal, Header, Icon, Grid, Segment, Rail } from 'semantic-ui-react'

import ReactCardFlip from 'react-card-flip';

import { Link } from "react-router-dom";

import Fade from "react-reveal/Fade";

import "../css/Contact.css";
import Footer from "../Footer";


const prefOptions = [
	{
		key: "1", value: "ph", text: "Phone"
	},

	{
		key: "2", value: "em", text: "Email"
	}
];


const Contact = () => {
  return(
    <>
      <ContactChoice />
      <Footer />
    </>
  )
};

const ContactChoice = () => {
	const [enqABack, setEnqABack] = useState('linear-gradient(to right, #396afc, #2948ff)');
	const [enqBBack, setEnqBBack] = useState('linear-gradient(to right, #f85032, #e73827)')

	const [textA, setTextA] = useState("BUSINESS PROJECT");
	const [textB, setTextB] = useState("FEEDBACK ENQUIRY");

	const [enqAWidth, setEnqAWidth] = useState("45%");
	const [enqBWidth, setEnqBWidth] = useState("45%");

	const [enqADisplay, setEnqADisplay] = useState("visible");
	const [enqBDisplay, setEnqBDisplay] = useState("visible");

	const [enqPad, setEnqPad] = useState("5rem");

	const [showBus, setShowBus] = useState(false);
	const [showFeed, setShowFeed] = useState(false);

	const handleAClicked = () => {
		setEnqBDisplay("none");
		setEnqAWidth("100%");
		setTextA("");
		setShowBus(!showBus);
	};

	const handleBClicked = () => {
		setEnqADisplay("none");
		setEnqBWidth("100%"); 
		setShowFeed(!showFeed);
	};

	const [flipped, setFlipped] = useState(false);

	const handleFlip = () => {
		setFlipped(!flipped);
	}

	if(props.flippedState == "true") {
		setFlipped(true);
	}

	return (
		<>
			<Divider horizontal>
					<Header as='h2'>
						<div>
							<span><Icon name='angle double left' /></span>
							<span>Contact Us</span>
							<span><Icon name='angle double right' /></span>
						</div>
					</Header>
				</Divider>
			<Fade cascade>
				<Segment padded basic>
					<div className="choiceContainer">
						<Segment className="enqA" style={{ backgroundImage: enqABack, width: enqAWidth, padding: enqPad, display: enqADisplay }}>
							<h1 onClick={handleAClicked}>{textA}</h1>
							{showBus && <ContactForm />}
						</Segment>

						<Segment className="enqB" style={{ backgroundImage: enqBBack, width: enqBWidth, padding: enqPad, display: enqBDisplay }}>
							<h1 onClick={handleAClicked}>{textB}</h1>
							{showBus && <ContactForm flipped="true" />}
						</Segment>
					</div>
				</Segment>
			</Fade>

			<Message>
					<Header as="h6">Switch To Business</Header>
					<Button primary onClick={handleFlip}>Switch</Button>
			</Message>
		</>
	);
};

const ContactForm = (props) => {
	return (
		<ReactCardFlip isFlipped={flipped} flipDirection="horizontal">
			<Segment>
				<Grid centered>
					<Grid.Column className="form-column">
						<Header as="h1">We Take Business Seriously</Header>
						<Card fluid>
							<Card.Content>
								<Form unstackable>
									<Form.Group widths={2}>
										<Form.Input label="Title" placeholder="Mr/Mrs" />
										<Form.Input label="FirstName" placeholder="FirstName" />
										<Form.Input label="LastName" placeholder="LastName" />
									</Form.Group>
									<Form.Group widths={2}>
										<Form.Input label="Phone" placeholder="Phone" />
										<Form.Input label="Email" placeholder="Email" />
									</Form.Group>
									<Form.Group widths={2}>
										<TextArea placeholder="Leave your message." />
									</Form.Group>
									<Button type="submit">Contact</Button>
								</Form>
							</Card.Content>
						</Card>
					</Grid.Column>
				  </Grid>
				<Message className="mesBox">
					<Header as="h6">Switch To Feedback</Header>
					<Button primary onClick={handleFlip}>Switch</Button>
				</Message>
			</Segment>

			<Segment>
				<Grid centered>
					<Grid.Column className="form-column">
						<Header as="h1">Let Us Know Your Thoughts</Header>
						<Card fluid>
							<Card.Content>
								<Form unstackable>
									<Form.Group widths={2}>
										<Form.Input label="Name" placeholder="Name" />
										<Form.Input label="Email" placeholder="Email" />
									</Form.Group>
									<Form.Group widths={2}>
										<TextArea placeholder="Leave your message." />
									</Form.Group>
									<Button type="submit">Contact</Button>
								</Form>
							</Card.Content>
						</Card>
					</Grid.Column>
				  </Grid>
				  <Message>
					<Header as="h6">Switch To Business</Header>
					<Button primary onClick={handleFlip}>Switch</Button>
				</Message>
			</Segment>
		</ReactCardFlip>
  	)
};


export default Contact;
