import React from "react";
import { Button, Header, Image, Modal } from 'semantic-ui-react';


var Stopwatch = function(elem, options) {
  
  var timer       = createTimer(),
      startButton = createButton("start", start),
      stopButton  = createButton("stop", stop),
      resetButton = createButton("reset", reset),
      offset,
      clock,
      interval;
  
  // default options
  options = options || {};
  options.delay = options.delay || 1;
 
  // append elements     
  elem.appendChild(timer);
  elem.appendChild(startButton);
  elem.appendChild(stopButton);
  elem.appendChild(resetButton);
  
  // initialize
  reset();
  
  // private functions
  function createTimer() {
    return document.createElement("span");
  }
  
  function createButton(action, handler) {
    var a = document.createElement("a");
    a.href = "#" + action;
    a.innerHTML = action;
    a.addEventListener("click", function(event) {
      handler();
      event.preventDefault();
    });
    return a;
  }
  
  function start() {
    if (!interval) {
      offset   = Date.now();
      interval = setInterval(update, options.delay);
    }
  }
  
  function stop() {
    if (interval) {
      clearInterval(interval);
      interval = null;
    }
  }
  
  function reset() {
    clock = 0;
    render(0);
  }
  
  function update() {
    clock += delta();
    render();
  }
  
  function render() {
    timer.innerHTML = clock/1000; 
  }
  
  function delta() {
    var now = Date.now(),
        d   = now - offset;
    
    offset = now;
    return d;
  }
  
  // public API
  this.start  = start;
  this.stop   = stop;
  this.reset  = reset;
};


// basic examples
var elems = document.getElementsByClassName("basic");

for (var i=0, len=elems.length; i<len; i++) {
  new Stopwatch(elems[i]);
}


// programmatic examples
var a = document.getElementById("a-timer");
aTimer = new Stopwatch(a);
aTimer.start();

// var b = document.getElementById("b-timer");
// bTimer = new Stopwatch(b, {delay: 100});
// bTimer.start();

// var c = document.getElementById("c-timer");
// cTimer = new Stopwatch(c, {delay: 456});
// cTimer.start();

// var d = document.getElementById("d-timer");
// dTimer = new Stopwatch(d, {delay: 1000});
// dTimer.start();

const size = "fullscreen";

function StopWatch() {
   const [open, setOpen] = React.useState(false)

   return (
      <Modal
        onClose={() => setOpen(false)}
        onOpen={() => setOpen(true)}
        open={open}
        size={size}
        trigger={<Button>Launch Script</Button>}
      >
      <Modal.Header>StopWatch</Modal.Header>
      
      <Modal.Content>
		    <div className="a-timer">
		    </div>
      </Modal.Content>
      
      <Modal.Actions>
        <Button color='red' onClick={() => setOpen(false)}>Close</Button>
        <Button
		      color='green'
          content="Done"
          labelPosition='right'
          icon='checkmark'
          onClick={() => setOpen(false)}
          positive
        />
      </Modal.Actions>
    </Modal>
  )
}

export default StopWatch;
