import React from "react";
import { Button, Header, Image, Modal } from 'semantic-ui-react';

import ReactStopwatch from 'react-stopwatch';

const size = "fullscreen";

function StopWatch() {
   const [open, setOpen] = React.useState(false);
   const [timer, setTimer] = useState(0);
   const [isActive, setIsActive] = useState(false);
   const [isPaused, setIsPaused] = useState(0);
   const countRef = useRef(null);

   const handleStart = () => {
      setIsActive(true);
      setIsPaused(true);
      countRef.current = setInterval(() => {
        setTimer((timer) => timer + 1)
      }, 1000)
   }

   const handlePause = () => {
     
   }

   const handleResume = () => {

   }

   const handleReset = () => {

   }

   return (
      <Modal
        onClose={() => setOpen(false)}
        onOpen={() => setOpen(true)}
        open={open}
        size={size}
        trigger={<Button>Launch Script</Button>}
      >
      <Modal.Header>StopWatch</Modal.Header>
      
      <Modal.Content>
        <div className="app">
          <h3>React Stopwatch</h3>
            <div className='stopwatch-card'>
              <p>{timer}</p> {/* here we will show timer */}
              <div className='buttons'>
                <button onClick={handleStart}>Start</button>
                <button onClick={handlePause}>Pause</button>
                <button onClick={handleResume}>Resume</button>
                <button onClick={handleReset}>Reset</button>
              </div>
            </div>
        </div>
      </Modal.Content>
      
      <Modal.Actions>
        <Button color='red' onClick={() => setOpen(false)}>Close</Button>
        <Button
		      color='green'
          content="Done"
          labelPosition='right'
          icon='checkmark'
          onClick={() => setOpen(false)}
          positive
        />
      </Modal.Actions>
    </Modal>
  )
}

export default StopWatch;
