import React, { useState } from 'react'
import { Button, Checkbox, Form, TextArea, Select } from 'semantic-ui-react'

import Fade from "react-reveal/Fade";

import "../css/Contact.css";
import Footer from "../Footer";


const Contact = () => {
  return(
    <>
      <ContactChoice />
      <Footer />
    </>
  )
}

const ContactChoice = () => {
	const [textA, setTextA] = useState('BUSINESS');
	const [textB, setTextB] = useState('FEEDBACK');
	const [enqAWidth, setEnqAWidth] = useState("50%");
	const [enqBWidth, setEnqBWidth] = useState("50%");

	const [enqPad, setEnqPad] = useState("5rem");

	const [showBus, setShowBus] = useState(false);
	const [showFeed, setShowFeed] = useState(false);

	return (
		<>
			<Fade cascade>
				<div className="choiceContainer">
					<div className="enqA" style={{ width: enqAWidth, padding: enqPad }} onClick={() => {setEnqAWidth("100%"); setEnqBWidth("0"); setTextB(""); setShowBus(!showBus); setEnqPad("0")}}>
						<h1>{textA}</h1>
					</div>
					{showBus && <ContactFormBusiness />}

					<div className="enqB" style={{width: enqBWidth, padding: enqPad}} onClick={() => {setEnqBWidth("100%"); setEnqAWidth("0"); setTextA(""); setShowFeed(!showFeed); setEnqPad("0")}}>
						<h1>{textB}</h1>
					</div>
					{showFeed && <ContactFormFeedback />}
				</div>
			</Fade>
		</>
	);
}

const prefOptions = [
	{
		key: "1", value: "Phone"
	},

	{
		key: "2", value: "Email"
	}
]

const ContactFormBusiness = () => (
  <Form>
    <Form.Field>
      <label>Name</label>
      <input placeholder='Name' />
    </Form.Field>
    <Form.Field>
      <label>Phone</label>
      <input placeholder='Phone' />
    </Form.Field>
    <Form.Field>
      <label>Email</label>
      <input placeholder='Email' />
    </Form.Field>
    <Select placeholder='Phone / Email' options={prefOptions} />
    <Form.Field>
      <label>Message</label>
      <TextArea placeholder="Leave you message and we will get back to you." />
      <Checkbox label='I agree to the Terms and Conditions' />
    </Form.Field>
    <Button type='submit'>Submit</Button>
  </Form>
)

const ContactFormFeedback = () => (
  <Form>
    <Form.Field>
      <label>First Name</label>
      <input placeholder='First Name' />
    </Form.Field>
    <Form.Field>
      <label>Last Name</label>
      <input placeholder='Last Name' />
    </Form.Field>
    <Form.Field>
      <Checkbox label='I agree to the Terms and Conditions' />
    </Form.Field>
    <Button type='submit'>Submit</Button>
  </Form>
)

export default Contact;
