import React, { useState } from 'react'
import { Divider, Card, Button, Checkbox, Form, TextArea, Select, Confirm, Modal, Header, Icon, Grid, Segment, Rail } from 'semantic-ui-react'

import { Link } from "react-router-dom";

import Fade from "react-reveal/Fade";

import "../css/Contact.css";
import Footer from "../Footer";


const prefOptions = [
	{
		key: "1", value: "ph", text: "Phone"
	},

	{
		key: "2", value: "em", text: "Email"
	}
]


const Contact = () => {
  return(
    <>
      <ContactChoice />
      <Footer />
    </>
  )
}

const ContactChoice = () => {
	const [enqAWidth, setEnqAWidth] = useState("45%");
	const [enqBWidth, setEnqBWidth] = useState("45%");

	const [enqADisplay, setEnqADisplay] = useState("visible");
	const [enqBDisplay, setEnqBDisplay] = useState("visible");

	const [enqPad, setEnqPad] = useState("5rem");

	const [showBus, setShowBus] = useState(false);
	const [showFeed, setShowFeed] = useState(false);

	const handleAClicked = () => {
		setEnqBDisplay("none");
		setEnqAWidth("100%"); 
		setShowBus(!showBus);
	}

	const handleBClicked = () => {
		setEnqADisplay("none");
		setEnqBWidth("100%"); 
		setShowFeed(!showFeed);
	}

	return (
		<>
			<Divider horizontal>
					<Header as='h2'>
						<div>
							<span><Icon name='angle double left' /></span>
							<span>Contact Us</span>
							<span><Icon name='angle double right' /></span>
						</div>
					</Header>
				</Divider>
			<Fade cascade>
				<Segment padded basic>
					<div className="choiceContainer">
						<Segment className="enqA" style={{ backgroundImage: 'linear-gradient(to right, #396afc, #2948ff)', width: enqAWidth, padding: enqPad, display: enqADisplay }} onClick={handleAClicked}>
							<h1>BUSINESS PROJECT</h1>
							{showBus && <ContactFormBusiness />}
						</Segment>

						<Segment className="enqB" style={{ backgroundImage: 'linear-gradient(to right, #f85032, #e73827)', width: enqBWidth, padding: enqPad, display: enqBDisplay }} onClick={handleBClicked}>
							<h1>FEEDBACK ENQUIRY</h1>
							{showFeed && <ContactFormFeedback />}
						</Segment>
					</div>
				</Segment>
			</Fade>
		</>
	);
}

const ContactFormBusiness = () => {
	return (
		<Segment>
			<Rail internal position='left'>
				<Segment>
					<Header as="h6">Switch To Feedback</Header>
					<Button primary>Switch</Button>
				</Segment>
			</Rail>
			<Grid centered>
				<Grid.Column className="form-column">
			  		<Header as="h1">Contact Us</Header>
			  		<Card fluid>
			  			<Card.Content>
			  				<Form unstackable>
			  					<Form.Group widths={2}>
			  						<Form.Input label="Title" placeholder="Mr/Mrs" />
			  						<Form.Input label="FirstName" placeholder="FirstName" />
			  						<Form.Input label="LastName" placeholder="LastName" />
			  					</Form.Group>
			  					<Form.Group widths={2}>
			  						<Form.Input label="Phone" placeholder="Phone" />
			  						<Form.Input label="Email" placeholder="Email" />
			  					</Form.Group>
			  					<Form.Group widths={2}>
			  						<TextArea placeholder="Leave your message." />
			  					</Form.Group>
			  					<Button type="submit">Contact</Button>
			  				</Form>
			  			</Card.Content>
			  		</Card>
			  	</Grid.Column>
			  </Grid>
		</Segment>
  	)
}

const ContactFormFeedback = () => {
	return (
		<Segment>
			<Rail internal position='left'>
				<Segment>
					<Header as="h6">Switch to Business</Header>
					<Button primary>Switch</Button>
				</Segment>
			</Rail>
			<Grid centered>
				<Grid.Column className="form-column">
			  		<Header as="h1">Contact Us</Header>
			  		<Card fluid>
			  			<Card.Content>
			  				<Form unstackable>
			  					<Form.Group widths={2}>
			  						<Form.Input label="Name" placeholder="Name" />
			  						<Form.Input label="Email" placeholder="Email" />
			  					</Form.Group>
			  					<Form.Group widths={2}>
			  						<TextArea placeholder="Leave your message." />
			  					</Form.Group>
			  					<Button type="submit">Contact</Button>
			  				</Form>
			  			</Card.Content>
			  		</Card>
			  	</Grid.Column>
			  </Grid>
		</Segment>
  	)
}

export default Contact;
