import React, { useState } from 'react'
import { Card, Button, Checkbox, Form, TextArea, Select, Confirm, Modal, Header, Icon, Grid, Segment } from 'semantic-ui-react'

import Fade from "react-reveal/Fade";

import "../css/Contact.css";
import Footer from "../Footer";


const prefOptions = [
	{
		key: "1", value: "ph", text: "Phone"
	},

	{
		key: "2", value: "em", text: "Email"
	}
]


const Contact = () => {
  return(
    <>
      <ContactChoice />
      <Footer />
    </>
  )
}

const ContactChoice = () => {
	const [textA, setTextA] = useState('BUSINESS');
	const [textB, setTextB] = useState('FEEDBACK');
	const [enqAWidth, setEnqAWidth] = useState("45%");
	const [enqBWidth, setEnqBWidth] = useState("45%");

	const [enqPad, setEnqPad] = useState("5rem");

	const [showBus, setShowBus] = useState(false);
	const [showFeed, setShowFeed] = useState(false);

	return (
		<>
			<Fade cascade>
				<div className="choiceContainer">
					<div className="enqA" style={{ width: enqAWidth, padding: enqPad }} onClick={() => {setEnqAWidth("100%"); setEnqBWidth("0"); setTextB(""); setShowBus(!showBus); setEnqPad("0")}}>
						<h1>{textA}</h1>
					</div>
					<div className="formBoxA">
						{showBus && <ContactFormBusiness />}
					</div>

					<div className="enqB" style={{width: enqBWidth, padding: enqPad}} onClick={() => {setEnqBWidth("100%"); setEnqAWidth("0"); setTextA(""); setShowFeed(!showFeed); setEnqPad("0")}}>
						<h1>{textB}</h1>
					</div>
					<div className="formBoxB">
						{showFeed && <ContactFormFeedback />}
					</div>
				</div>
			</Fade>
		</>
	);
}

const ContactFormBusiness = () => {
	return (
	  <Grid centered>
	  	<Grid.Column className="form-column">
	  		<Header as="h1">Contact Us</Header>
	  		<Card fluid>
	  			<Card.Content>
	  				<Form unstackable>
	  					<Form.Group widths={2}>
	  						<Form.Input label="Title" placeholder="Mr/Mrs" />
	  						<Form.Input label="FirstName" placeholder="FirstName" />
	  						<Form.Input label="LastName" placeholder="LastName" />
	  					</Form.Group>
	  					<Form.Group widths={2}>
	  						<Form.Input label="Phone" placeholder="Phone" />
	  						<Form.Input label="Email" placeholder="Email" />
	  					</Form.Group>
	  					<Form.Group widths={2}>
	  						<TextArea placeholder="Leave your message." />
	  					</Form.Group>
	  					<Button type="submit">Contact</Button>
	  				</Form>
	  			</Card.Content>
	  		</Card>
	  	</Grid.Column>
	  </Grid>
  )
}

const ContactFormFeedback = () => {
	return (
  <Grid centered>
  	<Grid.Column className="form-column">
  		<SemanticHeader>Contact Us</SemanticHeader>

  		<Card fluid>
  			<Card.Content>
  				<Form unstackable>
  					<Form.Group widths={2}>
  						<Form.Input label="Name" placeholder="Name" />
  						<Form.Input label="Email" placeholder="Email" />
  					</Form.Group>
  					<Form.Group widths={2}>
  						<TextArea placeholder="Leave your message." />
  					</Form.Group>
  					<Button type="submit">Contact</Button>
  				</Form>
  			</Card.Content>
  		</Card>
  	</Grid.Column>
  </Grid>
  )
}

export default Contact;
