import React, { Component, useState } from 'react'

import Fade from "react-reveal/Fade";
import Roll from "react-reveal/Roll";

import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

import Footer from "../Footer";


const Contact = () => {
  return(
    <>
      <ContactChoice />
      <ContactForm />
      <Footer />
    </>
  )
}

 class ContactChoice extends Component {
	constructor() {
		super()
		this.state = {
			enqWidth: "40%",
		}
	}

	expandSelectedA = e => {
		alert("Clicked");
		this.setState({
			enqWidth: "100%"
		})
	}

	expandSelectedB() {
		this.setState({
			enqWidth: "100%"
		})
	}

	render() {
		return (
			<>
				<Fade cascade>
					<div className="choiceContainer">
						<div className="enqA" style={{width: this.state.enqWidth}} onClick={() => this.expandSelectedA}>
							<h1>Business</h1>
						</div>

						<div className="enqB" style={{width: this.state.enqWidth}} onClick={() => this.expandSelectedB}>
							<h1>Feedback</h1>
						</div>
					</div>
				</Fade>
			</>
		);
	}
}

const ContactForm = () => {

	const [name, setName] = useState('');
	const [email, setEmail] = useState('');
	const [message, setMessage] = useState('');

	const handleSubmit = (e) => {
		e.preventDefault();
	}

	return (
		<>
			<article>
				<form className="form" onSubmit={handleSubmit}>
					<div className="form-control">
						<label htmlForm="name">Name: </label>
						<input type="text" id="name" name="name" value={ name } onChange={(e) => setName(e.target.value)} />
					</div>

					<div className="form-control">
						<label htmlForm="email">Name: </label>
						<input type="email" id="email" name="email" value={ email } onChange={(e) => setEmail(e.target.value)}/>
					</div>

					<div className="form-control">
						<textarea id="message" name="message" value={ message } onChange={(e) => setMessage(e.target.value)}></textarea>
					</div>
					<button type="submit">Contact</button>
				</form>
			</article>
		</>
	)
}

export default Contact;
