import React from "react";
import { Button, Header, Image, Modal } from 'semantic-ui-react';


// initialize your variables outside the function 
var count = 0; 
var clearTime; 
var seconds = 0, minutes = 0, hours = 0; 
var clearState; 
var secs, mins, gethours; 


function startWatch( ) { 
  /* check if seconds is equal to 60 and add a +1 to minutes, and set seconds to 0 */
   if ( seconds === 60 ) { 
     seconds = 0; 
     minutes = minutes + 1;
   } 

   /* you use the javascript tenary operator to format how the minutes should look and add 0 to minutes if less than 10 */ 
   mins = ( minutes < 10 ) ? ( '0' + minutes + ': ' ) : ( minutes + ': ' ); 

   /* check if minutes is equal to 60 and add a +1 to hours set minutes to 0 */ 
  if ( minutes === 60 ) { 
    minutes = 0; 
    hours = hours + 1;
  } 

  /* you use the javascript tenary operator to format how the hours should look and add 0 to hours if less than 10 */ 
  gethours = ( hours < 10 ) ? ( '0' + hours + ': ' ) : ( hours + ': ' ); 
  secs = ( seconds < 10 ) ? ( '0' + seconds ) : ( seconds ); 

  // display the stopwatch 
  var x = document .getElementById("timer"); 
  x.innerHTML = 'Time: ' + gethours + mins + secs; 

  /* call the seconds counter after displaying the stop watch and increment seconds by +1 to keep it counting */ 
  seconds++; 

  /* call the setTimeout( ) to keep the stop watch alive ! */ 

  clearTime = setTimeout( "startWatch( )", 1000 );
} 

startWatch( ) //create a function to start the stop watch

//create a function to stop the time 
function stopTime( ) { 
  /* check if seconds, minutes and hours are not equal to 0 */ 
  if ( seconds !== 0 || minutes !== 0 || hours !== 0 ) { 
    /* display the full time before reseting the stop watch */ 
    var fulltime = document .getElementById( "fulltime" ); 
    
    //display the full time 
    fulltime.style.display = "block"; 
    var time = gethours + mins + secs; 
    fulltime.innerHTML = 'Fulltime: ' + time; 
    
    // reset the stop watch 
    seconds = 0; 
    minutes = 0; 
    hours = 0;

    secs = '0' + seconds; 
    mins = '0' + minutes + ': '; 
    gethours = '0' + hours + ': '; 

    /* display the stopwatch after it's been stopped */ 
    var x = document.getElementById ("timer"); 
    var stopTime = gethours + mins + secs; x.innerHTML = stopTime; 

    /* display all stop watch control buttons */ 
    var showStart = document.getElementById ('start'); 
    showStart.style.display = "inline-block"; 
    var showStop = document.getElementById ("stop"); 
    showStop.style.display = "inline-block"; 

    /* clear the stop watch using the setTimeout( ) return value 'clearTime' as ID */ 
    clearTimeout( clearTime ); 
  } 

  // if () } // 
  stopTime() 

  /* you need to call the stopTime( ) function to terminate the stop watch */ 
  window.addEventListener( 'load', function ( ) { var stop = document.getElementById ("stop"); stop.addEventListener( 'click', stopTime ); });
}

  // stopwatch.js end

const size = "fullscreen";

function StopWatch() {
   const [open, setOpen] = React.useState(false)

   return (
      <Modal
        onClose={() => setOpen(false)}
        onOpen={() => setOpen(true)}
        open={open}
        size={size}
        trigger={<Button>Launch Script</Button>}
      >
      <Modal.Header>StopWatch</Modal.Header>
      
      <Modal.Content>
		    <div>
			    <h1>00.00.00</h1>
		    </div>
      </Modal.Content>
      
      <Modal.Actions>
        <Button color='red' onClick={() => setOpen(false)}>Close</Button>
        <Button
		      color='green'
          content="Done"
          labelPosition='right'
          icon='checkmark'
          onClick={() => setOpen(false)}
          positive
        />
      </Modal.Actions>
    </Modal>
  )
}

export default StopWatch;
