import React, { useState } from 'react'

import Fade from "react-reveal/Fade";

import "../css/Contact.css";
import Footer from "../Footer";


const Contact = () => {
  return(
    <>
      <ContactChoice />
      <ContactForm />
      <Footer />
    </>
  )
}

const ContactChoice = () => {
	const [enqAWidth, setEnqAWidth] = useState("40%");
	const [enqBWidth, setEnqBWidth] = useState("40%");

	return (
		<>
			<Fade cascade>
				<div className="choiceContainer">
					<div className="enqA" style={{ width: enqAWidth }} onClick={() => {setEnqAWidth("100%"); setEnqBWidth("0%")}}>
						<h1>Business</h1>
					</div>

					<div className="enqB" style={{width: enqBWidth}} onClick={() => {setEnqBWidth("100%"); setEnqAWidth("0%")}}>
						<h1>Feedback</h1>
					</div>
				</div>
			</Fade>
		</>
	);
}

const ContactForm = () => {

	const [name, setName] = useState('');
	const [email, setEmail] = useState('');
	const [message, setMessage] = useState('');

	const handleSubmit = (e) => {
		e.preventDefault();
	}

	return (
		<>
			<article>
				<form className="form" onSubmit={handleSubmit}>
					<div className="form-control">
						<label>Name: </label>
						<input type="text" id="name" name="name" value={ name } onChange={(e) => setName(e.target.value)} />
					</div>

					<div className="form-control">
						<label>Email: </label>
						<input type="email" id="email" name="email" value={ email } onChange={(e) => setEmail(e.target.value)}/>
					</div>

					<div className="form-control">
						<textarea id="message" name="message" value={ message } onChange={(e) => setMessage(e.target.value)}>Message...</textarea>
					</div>
					<button type="submit">Contact</button>
				</form>
			</article>
		</>
	)
}

export default Contact;
