import React, { useState } from 'react'
import { Card, Button, Checkbox, Form, TextArea, Select, Confirm, Modal, Header, Icon, Grid, Segment, Rail } from 'semantic-ui-react'

import { Link } from "react-router-dom";

import Fade from "react-reveal/Fade";

import "../css/Contact.css";
import Footer from "../Footer";


const prefOptions = [
	{
		key: "1", value: "ph", text: "Phone"
	},

	{
		key: "2", value: "em", text: "Email"
	}
]


const Contact = () => {
  return(
    <>
      <ContactChoice />
      <Footer />
    </>
  )
}

const ContactChoice = () => {
	const [textA, setTextA] = useState('BUSINESS');
	const [textB, setTextB] = useState('FEEDBACK');
	const [enqAWidth, setEnqAWidth] = useState("45%");
	const [enqBWidth, setEnqBWidth] = useState("45%");

	const [enqADisplay, setEnqADisplay] = useState("visible");
	const [enqBDisplay, setEnqBDisplay] = useState("visible");

	const [enqPad, setEnqPad] = useState("5rem");

	const [showBus, setShowBus] = useState(false);
	const [showFeed, setShowFeed] = useState(false);

	return (
		<>
			<Fade cascade>
				<Segment raised padded>
					<div className="choiceContainer">
						<Segment ssName="enqABox" style={{ background: "blue" }}>
							<div className="enqA" style={{ width: enqAWidth, padding: enqPad }} onClick={() => {setEnqAWidth("100%"); setEnqBDisplay("hidden"); setShowBus(!showBus); setEnqPad("0")}}>
								<h1>{textA}</h1>
							</div>
							{showBus && <ContactFormBusiness />}
						</Segment>

						<Segment style={{ background: "red" }}>
							<div className="enqB" style={{width: enqBWidth, padding: enqPad}} onClick={() => {setEnqBWidth("100%"); setEnqADisplay("hidden"); setShowFeed(!showFeed); setEnqPad("0")}}>
								<h1>{textB}</h1>
							</div>
							{showFeed && <ContactFormFeedback />}
						</Segment>
					</div>
					</Segment>
			</Fade>
		</>
	);
}

const ContactFormBusiness = () => {
	return (
		<Segment>
			<Rail internal position='left'>
				<Segment>
					<Header as="h6">Did you mean to click FeedBack?</Header>
					<Button primary>Yes</Button>
					<Button secondary>No</Button>
				</Segment>
			</Rail>
			<Grid centered>
				<Grid.Column className="form-column">
			  		<Header as="h1">Contact Us</Header>
			  		<Card fluid>
			  			<Card.Content>
			  				<Form unstackable>
			  					<Form.Group widths={2}>
			  						<Form.Input label="Title" placeholder="Mr/Mrs" />
			  						<Form.Input label="FirstName" placeholder="FirstName" />
			  						<Form.Input label="LastName" placeholder="LastName" />
			  					</Form.Group>
			  					<Form.Group widths={2}>
			  						<Form.Input label="Phone" placeholder="Phone" />
			  						<Form.Input label="Email" placeholder="Email" />
			  					</Form.Group>
			  					<Form.Group widths={2}>
			  						<TextArea placeholder="Leave your message." />
			  					</Form.Group>
			  					<Button type="submit">Contact</Button>
			  				</Form>
			  			</Card.Content>
			  		</Card>
			  	</Grid.Column>
			  </Grid>
		</Segment>
  	)
}

const ContactFormFeedback = () => {
	return (
		<Segment>
			<Rail internal position='left'>
				<Segment>
					<Header as="h6">Did you mean to click Business?</Header>
					<Button primary>Yes</Button>
					<Button secondary>No</Button>
				</Segment>
			</Rail>
			<Grid centered>
				<Grid.Column className="form-column">
			  		<Header as="h1">Contact Us</Header>
			  		<Card fluid>
			  			<Card.Content>
			  				<Form unstackable>
			  					<Form.Group widths={2}>
			  						<Form.Input label="Name" placeholder="Name" />
			  						<Form.Input label="Email" placeholder="Email" />
			  					</Form.Group>
			  					<Form.Group widths={2}>
			  						<TextArea placeholder="Leave your message." />
			  					</Form.Group>
			  					<Button type="submit">Contact</Button>
			  				</Form>
			  			</Card.Content>
			  		</Card>
			  	</Grid.Column>
			  </Grid>
		</Segment>
  	)
}

export default Contact;
