import React, { Component } from "react";
import "./ScriptContent.css";
import Fade from 'react-reveal/Fade';
import Rotate from 'react-reveal/Rotate';
import Bounce from 'react-reveal/Bounce';
import WheelPicker from 'react-simple-wheel-picker';


const data = [
	{
		id: '1',
		title: 'Stopwatch',
		value: 'A Stopwatch program that allows saving of times to database.',
		langs: 'Javascript PHP'
	},
	{
		id: '2',
		title: 'Magic 8 Ball',
		value: 'The infamous magic 8 ball, has been used for many things over the years',
		langs: 'Javascript PHP'
	}

]


class ScriptContent extends Component {
	render() {
    	return (
    		<div className="container">
    			<WheelPicker
            		data={data}
            		onChange={handleOnChange}
            		height={150}
            		width={100}
            		titleText="Enter value same as aria-label"
            		itemHeight={30}
            		selectedID={data[0].id}
            		color="#ccc"
            		activeColor="#333"
            		backgroundColor="#fff"
        		/>
     		</div>
    	);
    }
}

export default ScriptContent;
