import React, { Component } from "react";
import "./ScriptContent.css";
import Fade from 'react-reveal/Fade';
import Rotate from 'react-reveal/Rotate';
import Bounce from 'react-reveal/Bounce';


class Wheel extends Compoenent {
    constructor(props) {
        super(props)

        this.state = {
            radius: 150,
            cards: [],
            theta: 0.0
        }

        this.temp_theta = 0.0
    }

    componentDidMount() {
        let center_of_wheel = {
            x: parseFloat(this.wheel.style.width) / 2,
            y: parseFloat(this.wheel.style.height) / 2
        }

        let temp_cards = [];

        for(let i=0; i < 8; i++) {
            temp_cards.push(
                <Card radius={this.state.radius} radian_interval={(MATH.PI / 4) * i} center={center_of_wheel} key={`card_${i}`} />
            )
        }

        this.setState({ cards: temp_cards });
    }

    render() {
        return(
            <div ref={ref_id => this.wheel = ref_id} styles={styles.wheel}>
                
            </div>
        )
    }
}

const styles = {
    wheel: {
        margin: '0',
        padding: '0',
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        height: '100px',
        width: '100px',
        backgroundColor: 'red',
        borderRadius: '50px'
    }
}

export default Wheel;
