import React, { Component } from "react";
import ContactForm from "./ContactForm";
import Fade from "react-reveal/Fade";

import "./ContactChoice.css";


class ContactChoice extends Component {
	constructor() {
		super()
		this.state = {
			enqWidth: "40%",
		}
	}

	expandSelectedA = e => {
		this.setState({
			enqWidth: "100%"
		})
	}

	expandSelectedB() {
		this.setState({
			enqWidth: "100%"
		})
	}

	render() {
		return (
			<>
				<Fade cascade>
					<div className="choiceContainer">
						<div className="enqA" style={{width: this.state.enqWidth}} onClick={() => this.expandSelectedA}>
							<h1>Business</h1>
						</div>
					
						<div className="enqB" style={{width: this.state.enqWidth}} onClick={() => this.expandSelectedB}>
							<h1>Feedback</h1>
						</div>
					</div>
				</Fade>
			</>
		);
	}
}

export default ContactChoice;
