import React, { useState } from 'react'
import { Card, Button, Checkbox, Form, TextArea, Select, Confirm, Modal, Header, Icon } from 'semantic-ui-react'
import { Grid, Header as SemanticHeader } from 'semantic-ui-react'

import Fade from "react-reveal/Fade";

import "../css/Contact.css";
import Footer from "../Footer";


const Contact = () => {
  return(
    <>
      <ContactChoice />
      <Footer />
    </>
  )
}

const ContactChoice = () => {
	const [textA, setTextA] = useState('BUSINESS');
	const [textB, setTextB] = useState('FEEDBACK');
	const [enqAWidth, setEnqAWidth] = useState("45%");
	const [enqBWidth, setEnqBWidth] = useState("45%");

	const [enqPad, setEnqPad] = useState("5rem");

	const [showBus, setShowBus] = useState(false);
	const [showFeed, setShowFeed] = useState(false);

	return (
		<>
			<Fade cascade>
				<div className="choiceContainer">
					<div className="enqA" style={{ width: enqAWidth, padding: enqPad }} onClick={() => {setEnqAWidth("100%"); setEnqBWidth("0"); setTextB(""); setShowBus(!showBus); setEnqPad("0")}}>
						<h1>{textA}</h1>
					</div>
					<div className="formBoxA">
						{showBus && <ContactFormBusiness />}
					</div>

					<div className="enqB" style={{width: enqBWidth, padding: enqPad}} onClick={() => {setEnqBWidth("100%"); setEnqAWidth("0"); setTextA(""); setShowFeed(!showFeed); setEnqPad("0")}}>
						<h1>{textB}</h1>
					</div>
					<div className="formBoxB">
						{showFeed && <ContactFormFeedback />}
					</div>
				</div>
			</Fade>
		</>
	);
}

const prefOptions = [
	{
		key: "1", value: "ph", text: "Phone"
	},

	{
		key: "2", value: "em", text: "Email"
	}
]

function BusineesThanks()
{
	const [open, setOpen] = React.useState(false)

	return (
	<Modal
	basic
	onClose={() => setOpen(false)}
	onOpen={() => setOpen(true)}
	open={open}
	size='small'
	trigger={<Button>Basic Modal</Button>}
	>
	<Header icon>
	<Icon name='archive' />
	Archive Old Messages
	</Header>
	<Modal.Content>
	<p>
	Your inbox is getting full, would you like us to enable automatic
	archiving of old messages?
	</p>
	</Modal.Content>
	<Modal.Actions>
	<Button basic color='red' inverted onClick={() => setOpen(false)}>
	<Icon name='remove' /> No
	</Button>
	<Button color='green' inverted onClick={() => setOpen(false)}>
	<Icon name='checkmark' /> Yes
	</Button>
	</Modal.Actions>
	</Modal>
	)
}

const ContactFormBusiness = () => {
	const handleSubmit = () => {
		return (
			<BusineesThanks />
		)
	}

	return (
  <Grid centered>
  	<Grid.Column className="form-column">
  		<SemanticHeader>Contact Us</SemanticHeader>
  		<Card fluid>
  			<Card.Content>
  				<Form unstackable onSubmit={handleSubmit}>
  					<Form.Group widths={2}>
  						<Form.Input label="Title" placeholder="Mr/Mrs" />
  						<Form.Input label="FirstName" placeholder="FirstName" />
  						<Form.Input label="LastName" placeholder="LastName" />
  					</Form.Group>
  					<Form.Group widths={2}>
  						<Form.Input label="Phone" placeholder="Phone" />
  						<Form.Input label="Email" placeholder="Email" />
  					</Form.Group>
  					<Form.Group widths={2}>
  						<TextArea placeholder="Leave your message." />
  					</Form.Group>
  					<Button type="submit">Contact</Button>
  				</Form>
  			</Card.Content>
  		</Card>
  	</Grid.Column>
  </Grid>
  )
}

const ContactFormFeedback = () => {
	return (
  <Grid centered>
  	<Grid.Column className="form-column">
  		<SemanticHeader>Contact Us</SemanticHeader>

  		<Card fluid>
  			<Card.Content>
  				<Form unstackable>
  					<Form.Group widths={2}>
  						<Form.Input label="Name" placeholder="Name" />
  						<Form.Input label="Email" placeholder="Email" />
  					</Form.Group>
  					<Form.Group widths={2}>
  						<TextArea placeholder="Leave your message." />
  					</Form.Group>
  					<Button type="submit">Contact</Button>
  				</Form>
  			</Card.Content>
  		</Card>
  	</Grid.Column>
  </Grid>
  )
}

export default Contact;
